css:
    * {
        margin: 0;
        padding: 0;
        box-sizing: border-box;
        font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    }

    body {
        background-color: #f5f5f5;
        display: flex;
        justify-content: center;
    }

    .journal-container {

        background: white;
        padding: 5px;
        box-shadow: 0 0 15px rgba(0, 0, 0, 0.1);
        border: 1px solid #ddd;
    }

  .header-content {
    display: flex;
    align-items: center;
    justify-content: space-between;
    position: relative;
}

.logo-section {
    flex: 0 0 auto;
    text-align: left;
    /*padding-top:20px;*/
}

.company-logo {
    max-height: 170px;
    max-width: 190px;
    object-fit: contain;
}

.company-section {
    flex: 1;
    text-align: center;
    padding: 0 10px;
}

.company-section h1 {
    font-size: 28px;
    color: #2c3e50;
    margin-bottom: 5px;
    font-weight: bold;
}

.company-section h2 {
    font-size: 20px;
    color: #34495e;
    margin-bottom: 8px;
    font-weight: 600;
}

.company-info p {
    font-size: 14px;
    color: #7f8c8d;
    font-style: italic;
    margin: 0;
}

.invoice-section {
    flex: 0 0 auto;
    text-align: right;
    width:25%;
    margin-right:15px;
}

.invoice-number {
    background: #f8f9fa;
    padding: 12px 15px;
    border: 1px solid #dee2e6;
    border-radius: 6px;
    font-size: 14px;
}

.invoice-number div {
    margin-bottom: 5px;
}

.invoice-number div:last-child {
    margin-bottom: 0;
}

.invoice-number strong {
    color: #2c3e50;
}

    .invoice-container {
        /*width: 800px;*/
        background: white;
        padding: 10px;
        box-shadow: 0 0 15px rgba(0, 0, 0, 0.1);
        border: 1px solid #ddd;
    }




    .company-info {
        margin: 10px 0;
        font-size: 14px;
        color: #555;
    }

    .info-section {
        display: flex;
        justify-content: space-between;
        margin-bottom: 10px;
    }


    .document-section {
        flex: 0 0 auto;
        text-align: right;
        width:25%;
    }

    .document-number {
        background: #f8f9fa;
        padding: 12px 15px;
        border: 1px solid #dee2e6;
        border-radius: 6px;
        font-size: 14px;
    }

    .document-number div {
        margin-bottom: 5px;
    }

    .document-number div:last-child {
        margin-bottom: 0;
    }

    .document-number strong {
        color: #2c3e50;
    }

    .entry-type-badge {
        display: inline-block;
        padding: 4px 12px;
        border-radius: 20px;
        font-size: 12px;
        font-weight: bold;
        margin-top: 5px;
    }

    .cash-payment {
        background: #ffeaa7;
        color: #d35400;
    }

    .cash-received {
        background: #55efc4;
        color: #00b894;
    }

    .general-entry {
        background: #74b9ff;
        color: #0984e3;
    }

    .info-section {
        display: flex;
        justify-content: space-between;
        margin-bottom: 20px;
    }

    .entry-info, .accounting-info {
        width: 48%;
        border: 1px solid #ddd;
        padding: 15px;
        border-radius: 4px;
        background-color: #f9f9f9;
    }

    .section-title {
        font-size: 16px;
        font-weight: bold;
        margin-bottom: 10px;
        color: #2c3e50;
        border-bottom: 1px solid #ddd;
        padding-bottom: 5px;
    }

    .info-row {
        display: flex;
        margin-bottom: 8px;
        font-size: 14px;
    }

    .info-label {
        width: 40%;
        font-weight: bold;
        color: #555;
    }

    .info-value {
        width: 60%;
        color: #333;
    }

    .accounts-table {
        width: 100%;
        border-collapse: collapse;
        margin-bottom: 20px;
        font-size: 14px;
    }

    .accounts-table th {
        background-color: #2c3e50;
        color: white;
        padding: 12px;
        text-align: center;
        border: 1px solid #ddd;
        font-weight: 600;
    }

    .accounts-table td {
        padding: 10px;
        border: 1px solid #ddd;
        text-align: center;
    }

    .accounts-table tr:nth-child(even) {
        background-color: #f8f9fa;
    }

    .debit-amount {
        color: #c0392b;
        font-weight: bold;
    }

    .credit-amount {
        color: #27ae60;
        font-weight: bold;
    }

    .summary-table {
        width: 60%;
        margin-left: auto;
        border-collapse: collapse;
        margin-bottom: 20px;
        font-size: 15px;
    }

    .summary-table th, .summary-table td {
        padding: 12px;
        border: 1px solid #ddd;
        text-align: center;
    }

    .summary-table th {
        background-color: #34495e;
        color: white;
        font-weight: 600;
    }

    .summary-table td {
        font-weight: bold;
        background-color: #f8f9fa;
    }


    .signature-section {
        display: flex;
        justify-content: space-between;
        margin-top: 20px;
        padding-top: 10px;
        border-top: 1px solid #ddd;
    }

    .signature-box {
        width: 45%;
        text-align: center;
    }

    .signature-line {
        margin-top: 40px;
        border-top: 1px solid #333;
        width: 80%;
        margin-left: auto;
        margin-right: auto;
    }

    .footer {
        margin-top: 20px;
        text-align: center;
        font-size: 12px;
        padding: 15px;
        background-color: #f8f9fa;
        border-radius: 4px;
        border-top: 2px solid #2c3e50;
        color: #555;
        bottom:0;
    }


    .terms-conditions {
        margin-top: 20px;
        padding: 10px;
        font-size: 12px;
        color: #777;
        border-top: 1px dashed #ddd;
    }

    .journal-container {
        padding-bottom: 120px;
    }
